SELECT AL1.YEAR, AL1.UNIT, AL1.DIVISION, COUNT (DISTINCT (AL1.STUDENT_SSN)), AL1.TOT_MINUS_STUDY_AWAY_CREDITS FROM UWSA.STUDENT_ANALYTICAL AL1 WHERE ((AL1.YEAR>=200102 AND AL1.TERM='1' AND AL1.RES_STATUS='R' AND AL1.TOT_MINUS_STUDY_AWAY_FLAG='Y' AND AL1.TOT_MINUS_STUDY_AWAY_STDNT_LVL IN ('1', '2'))) GROUP BY AL1.YEAR, AL1.UNIT, AL1.DIVISION, AL1.TOT_MINUS_STUDY_AWAY_CREDITS
